mapper.select:


  select 
    	category,replyCnt, num, title, content, nickName, regdate, hitCnt
  from 
      (
     select /*+INDEX_DESC(DK_BOARD PK_DK_BOARD) */ rownum rn, category,replyCnt, num, title, content, 

	 nickName, regdate, hitCnt from DK_BOARD where category = #{category} and




      rownum <= #{pageNum} * #{amount}
      )
  where rn > (#{pageNum} -1) * #{amount}
